name: Migrate area:* → bot:* + comp:*

on:
  workflow_dispatch:
    inputs:
      bot_label:
        description: "bot:* label to apply (leave blank to auto-detect from repo name)"
        required: false
        default: ""
        type: string
      state:
        description: "Which items to migrate"
        required: false
        default: "open"
        type: choice
        options:
          - open
          - all

jobs:
  migrate:
    runs-on: ubuntu-latest
    permissions:
      contents: read
      issues: write
      pull-requests: write
    steps:
      - name: Run migration
        uses: actions/github-script@v7
        with:
          script: |
            const inputBot = (core.getInput('bot_label') || '').trim();
            const state = core.getInput('state') || 'open';
            const { owner, repo } = context.repo;

            // Auto-detect bot:* from repo name when input is empty
            function detectBotLabel(repoName) {
              const r = repoName.toLowerCase();
              if (r.includes('reminder'))    return 'bot:reminder';
              if (r.includes('welcome'))     return 'bot:welcomecrew';
              if (r.includes('match'))       return 'bot:matchmaker';
              if (r.includes('achiev') ||
                  r.includes('claim')  ||
                  r.includes('shard'))       return 'bot:achievements';
              return null;
            }

            let BOT_LABEL = inputBot || detectBotLabel(repo);
            const allowed = new Set(['bot:reminder','bot:welcomecrew','bot:matchmaker','bot:achievements']);
            if (!BOT_LABEL || !allowed.has(BOT_LABEL)) {
              core.setFailed(`Could not determine a valid bot label. Got '${BOT_LABEL ?? ''}'. Pass workflow input 'bot_label' explicitly (one of ${[...allowed].join(', ')}).`);
              return;
            }

            // Map old area labels → new labels to add (besides the bot label)
            const map = new Map([
              ['area:reminder',       ['bot:reminder']],
              ['area:welcomecrew',    ['bot:welcomecrew']],
              ['area:matchmaker',     ['bot:matchmaker']],
              ['area:achievements',   ['bot:achievements']],
              ['area:claims',         ['bot:achievements']],   // historical alias
              ['areaclaims',          ['bot:achievements']],   // historical alias
              ['area:shards',         ['comp:shards']],
              ['areashards',          ['comp:shards']],        // historical alias
              ['area:ops',            ['comp:ops-contract']],
              ['areaops',             ['comp:ops-contract']],  // historical alias
            ]);

            // Helper: safe add (no blanks)
            const addIfValid = (set, name) => {
              if (typeof name === 'string') {
                const v = name.trim();
                if (v.length > 0) set.add(v);
              }
            };

            // Fetch all issues/PRs with pagination
            const items = await github.paginate(
              github.rest.issues.listForRepo,
              { owner, repo, state, per_page: 100 }
            );

            let changed = 0, scanned = 0;

            for (const it of items) {
              scanned++;
              const current = (it.labels || []).map(l => typeof l === 'string' ? l : l.name).filter(Boolean);
              const set = new Set(current);

              // Ensure exactly one bot:* (if none present, add the chosen one)
              if (![...set].some(n => typeof n === 'string' && n.startsWith('bot:'))) {
                addIfValid(set, BOT_LABEL);
              }

              // Replace mapped area:* labels
              let didReplace = false;
              for (const [oldName, addList] of map.entries()) {
                if (set.has(oldName)) {
                  set.delete(oldName);
                  for (const n of addList) addIfValid(set, n);
                  didReplace = true;
                }
              }

              if (!didReplace) continue; // nothing to do

              await github.rest.issues.setLabels({
                owner, repo, issue_number: it.number, labels: [...set]
              });
              changed++;
            }

            core.notice(`Scanned ${scanned} items; updated ${changed}. BOT_LABEL=${BOT_LABEL}, state=${state}`);

name: Batch create issues

on:
  workflow_dispatch:
    inputs:
      file:
        description: "Path to issues JSON or YAML inside this repo"
        required: true
        default: ".github/issue-batches/issues.json"

jobs:
  create:
    runs-on: ubuntu-latest
    permissions:
      issues: write
      contents: read
    steps:
      - uses: actions/checkout@v4

      - name: Ensure jq & yq
        run: |
          sudo apt-get update
          sudo apt-get install -y jq yq

      - name: Validate input file exists
        id: pick
        run: |
          FILE="${{ github.event.inputs.file }}"
          if [ ! -f "$FILE" ]; then
            echo "File not found: $FILE" >&2
            exit 1
          fi
          echo "file=$FILE" >> "$GITHUB_OUTPUT"

      - name: Normalize issues file to JSON
        run: |
          FILE="${{ steps.pick.outputs.file }}"
          case "$FILE" in
            *.yml|*.yaml) yq -o=json "$FILE" > issues.json ;;
            *.json) cp "$FILE" issues.json ;;
            *) echo "Unsupported file type: $FILE" >&2; exit 1 ;;
          esac
          echo "Normalized count:"
          jq 'length' issues.json

      - name: Create labels and issues (GitHub API)
        uses: actions/github-script@v7
        with:
          script: |
            const fs = require('fs');
            const repo = { owner: context.repo.owner, repo: context.repo.repo };
            const raw = fs.readFileSync('issues.json','utf8');

            // Accept either a flat array OR { milestone, defaults, issues:[...] }
            let payload;
            try { payload = JSON.parse(raw); }
            catch (e) { core.setFailed("issues.json is not valid JSON: " + e.message); return; }

            let items = [];
            let milestoneTitle = null;
            let defaultLabels = [];
            let defaultAssignees = [];

            if (Array.isArray(payload)) {
              items = payload;
            } else if (payload && typeof payload === 'object' && Array.isArray(payload.issues)) {
              milestoneTitle   = payload.milestone || null;
              defaultLabels    = Array.isArray(payload.defaults?.labels) ? payload.defaults.labels : [];
              defaultAssignees = Array.isArray(payload.defaults?.assignees) ? payload.defaults.assignees : [];
              items = payload.issues.map(it => {
                const labels = [...new Set([...(it.labels || []), ...defaultLabels])];
                let body = it.body || "";
                if (Array.isArray(it.acceptance_criteria) && it.acceptance_criteria.length) {
                  body += "\n\nAcceptance\n- " + it.acceptance_criteria.join("\n- ");
                }
                const assignees = Array.isArray(it.assignees) ? it.assignees : defaultAssignees;
                return { title: it.title, body, labels, assignees };
              });
            } else {
              core.setFailed("Issues file must be an array or { milestone, defaults, issues:[...] }.");
              return;
            }

            core.info(`Found ${items.length} issues in batch`);

            // Ensure labels exist (standard + any custom from the batch)
            const std = [
              "severity:critical","severity:high","severity:medium","severity:low",
              "area:scheduler","area:infra","area:reminders","area:data","area:commands","area:ops","area:devx",
              "type:bug","type:perf","type:hardening","type:security","type:tooling"
            ];
            const custom = [...new Set(items.flatMap(i => Array.isArray(i.labels) ? i.labels : []))]
              .filter(x => !std.includes(x));
            const allLabels = [...new Set([...std, ...custom])];

            for (const name of allLabels) {
              try {
                await github.rest.issues.createLabel({ ...repo, name, color: "cccccc", description: name });
                core.info(`Created label: ${name}`);
              } catch (e) {
                if (e.status === 422) core.info(`Label exists: ${name}`);
                else core.warning(`Label "${name}" error: ${e.message}`);
              }
            }

            // Optional milestone
            let milestoneNumber = undefined;
            if (milestoneTitle) {
              const list = await github.rest.issues.listMilestones({ ...repo, state: "open" });
              const found = list.data.find(m => m.title === milestoneTitle);
              if (found) milestoneNumber = found.number;
              else {
                const created = await github.rest.issues.createMilestone({ ...repo, title: milestoneTitle });
                milestoneNumber = created.data.number;
              }
              core.info(`Using milestone: ${milestoneTitle} #${milestoneNumber}`);
            }

            // Create issues
            for (const it of items) {
              await github.rest.issues.create({
                ...repo,
                title: it.title || "(missing title)",
                body: it.body || "",
                labels: Array.isArray(it.labels) ? it.labels : [],
                assignees: Array.isArray(it.assignees) ? it.assignees : [],
                milestone: milestoneNumber
              });
              core.info(`Created: ${it.title}`);
            }

name: Add issues to Cross-Bot Hotlist
on:
  issues:
    types: [opened, reopened, labeled, edited, transferred]

jobs:
  add:
    runs-on: ubuntu-latest
    steps:
      - name: Set Priority field from P* label
        env:
          GH_TOKEN: ${{ secrets.PROJECTS_TOKEN }}
          PROJECT_OWNER: cailleanC1C
          PROJECT_NUMBER: 1   # <-- just the number, e.g. 5 (no quotes)
          ISSUE_NODE_ID: ${{ github.event.issue.node_id }}
          ISSUE_LABELS_JSON: ${{ toJson(github.event.issue.labels) }}
        run: |
          set -euo pipefail

          # 1) Determine P* from labels
          PRIORITY=$(printf '%s' "$ISSUE_LABELS_JSON" | jq -r 'map(.name) | map(select(test("^P[0-4]$"))) | first // empty')
          if [ -z "$PRIORITY" ]; then
            echo "No P* label; skipping Priority update."
            exit 0
          fi
          echo "Detected priority: $PRIORITY"

          # 2) Build a query with PROJECT_NUMBER as a literal Int
          read -r -d '' QUERY <<'GRAPHQL' || true
          query($owner:String!) {
            user(login:$owner) {
              projectV2(number: __NUM__) {
                id
                fields(first:50) {
                  nodes {
                    ... on ProjectV2SingleSelectField {
                      id
                      name
                      options { id name }
                    }
                  }
                }
              }
            }
          }
          GRAPHQL
          QUERY="${QUERY/__NUM__/${PROJECT_NUMBER}}"

          # 3) Fetch project + Priority field + options
          PROJ_JSON=$(gh api graphql -F owner="$PROJECT_OWNER" -f query="$QUERY")
          PROJECT_ID=$(jq -r '.data.user.projectV2.id' <<<"$PROJ_JSON")
          FIELD_ID=$(jq -r '.data.user.projectV2.fields.nodes[] | select(.name=="Priority") | .id' <<<"$PROJ_JSON")
          OPTION_ID=$(jq -r --arg P "$PRIORITY" '.data.user.projectV2.fields.nodes[] | select(.name=="Priority") | .options[] | select(.name==$P) | .id' <<<"$PROJ_JSON")

          if [ -z "$PROJECT_ID" ] || [ -z "$FIELD_ID" ] || [ -z "$OPTION_ID" ]; then
            echo "Could not resolve project/field/option ids"; exit 1
          fi

          # 4) Ensure the item exists in the project; get item id
          ITEMS_JSON=$(gh api graphql -F id="$ISSUE_NODE_ID" -f query='
            query($id:ID!) {
              node(id:$id) {
                ... on Issue {
                  projectItems(first:50, includeArchived:false) {
                    nodes { id project { id } }
                  }
                }
              }
            }')
          ITEM_ID=$(jq -r --arg PID "$PROJECT_ID" '.data.node.projectItems.nodes[] | select(.project.id==$PID) | .id' <<<"$ITEMS_JSON")

          if [ -z "$ITEM_ID" ] || [ "$ITEM_ID" = "null" ]; then
            ADD_JSON=$(gh api graphql -F projectId="$PROJECT_ID" -F contentId="$ISSUE_NODE_ID" -f query='
              mutation($projectId:ID!, $contentId:ID!) {
                addProjectV2ItemById(input:{projectId:$projectId, contentId:$contentId}) { item { id } }
              }')
            ITEM_ID=$(jq -r '.data.addProjectV2ItemById.item.id' <<<"$ADD_JSON")
          fi

          # 5) Set the Priority value
          gh api graphql \
            -F projectId="$PROJECT_ID" \
            -F itemId="$ITEM_ID" \
            -F fieldId="$FIELD_ID" \
            -F optionId="$OPTION_ID" \
            -f query='
              mutation($projectId:ID!, $itemId:ID!, $fieldId:ID!, $optionId:String!) {
                updateProjectV2ItemFieldValue(input:{
                  projectId:$projectId,
                  itemId:$itemId,
                  fieldId:$fieldId,
                  value:{ singleSelectOptionId:$optionId }
                }) { clientMutationId }
              }'
          echo "Priority updated to $PRIORITY."
